"use server"
//[imports]
import User from "@/models/user.model"; import { connectToDatabase } from "@/lib/database"; 
//[CredentialsBase] 
//[OAuthBase]  
export async function GetUserByEmail(email: string) {
  try {
    connectToDatabase();
    const user = await User.findOne({ email });
    return user;
  } catch (error) {
    console.log(error);
  }
} 
 export async function GetUserById(id: string) {
  try {
    connectToDatabase();

    const user = await User.findOne({ _id: id });
    if (!user) {
      return { error: "User not found" };
    }
    return user;
  } catch (error) {
    console.log(error);
  }
}
