 import { auth } from "@/auth";
export async function currentUserServer() {
  try {
    const session = await auth();
    if (session) {
      return session.user;
    }
    return null;
  } catch (error) {
    console.log(error);
  }
} 

export const useCurrentUser=
 () => {
        const session = useSession();
              return session.data?.user;
  };
