export async function updateUserNameUser(
  values: z.infer<typeof UpdateUserSchema>,
  pathname: string,
) {
  try {
    const validatedFields = UpdateUserSchema.safeParse(values);
    if (!validatedFields.success) {
      return { error: "Please provide a valid email and password" };
    }
    const { name, username } = values;
    const userSession = await currentUser();
    if (!userSession) {
      return null;
    }
    if (!/^[a-zA-Z0-9_]+$/.test(values.username)) {
    return { error: 
        "Username can only contain letters, numbers, and underscores",
    }
    }

    // Check if the new username is already taken
    const existingUser = await database.user.findUnique({
      where: { username },
    });

    if (existingUser && existingUser.id !== userSession.id) {
      return { error: "Username already taken" };
    }

    const dataToUpdate: { name: string; username?: string } =
      name === userSession.name ? { username } : { name, username };

    const updatedUser = await database.user.update({
      where: {
        email: userSession.email!,
      },
      data: dataToUpdate,
    });

    if (!updatedUser) {
      return { error: "User not found" };
    }

    userSession.name = updatedUser.name;
    userSession.username = updatedUser.username;

    revalidatePath(pathname);

    return { success: "User updated" };
  } catch (error) {
    console.error(error);
    throw error;
  }
}

